# !/usr/bin/env python# -*- coding:utf-8 -*-# author : sjsimport random# print(random.random())print(random.randint(1,8)) # 包括8print(random.choice('hello')) # 随机取序列化中的元素print(random.sample('hello',2)) # 随机取序列中的指定数目的元素print(random.randrange(1,3)) # 随机取范围内的数 不包括最后一位def v_code():    code = ''    for i in range(5):        code += random.choice([str(random.randrange(10)),chr(random.randint(65,90))])    print(code)    return codev_code()# 1. 列表生成式  [x for x in range(10)]# 2. 生成器 （generator object）# 能调用的方法是 next 和 send#